/* Vehicle Maintenance & Commission Dashboard (PHPRunner Event - pure PHP)
 * - Year filter (All years = rolling last 12 months)
 * - Maintenance summary cards (clickable) -> Regional breakdown modal
 * - Line chart: cumulative Requested vs Completed + Outstanding
 * - Cross-tab: Vehicles √ó Months (Completed only), paginated (10 per page)
 * - Commission snapshot (STATION only): In-commission vs Out-of-commission with %
 * - Regional WOs table: Pending, Completed, Overdue>14d, Total, Avg TAT
 */

//////////////// Helpers ////////////////
function fetchOne($sql){ $rs = DB::Query($sql); return $rs ? $rs->fetchAssoc() : null; }
function fmtInt($n){ return number_format((int)$n); }
function fmtFloat($n,$d=1){ return number_format((float)$n,$d); }
function h($s){ return htmlspecialchars((string)$s, ENT_QUOTES); }
function pageLink($p,$yr){ return '?year='.rawurlencode($yr).'&page='.(int)$p; }

// Safer DB::Query with inline optional error echo (comment echo in prod)
function safeQuery($sql){
    $rs = DB::Query($sql);
    if($rs === false){
        // echo "<div style='color:#b91c1c;background:#fee2e2;padding:6px 10px;border-radius:6px;margin:6px 0;font-size:12px;'>SQL error: ".h(DB::LastError())."<br><code>".h($sql)."</code></div>";
        return null;
    }
    return $rs;
}

//////////////// Inputs ////////////////
$yearParam = isset($_GET['year']) ? trim($_GET['year']) : date('Y');
$selYear   = (strtolower($yearParam)==='all'||$yearParam==='') ? 'all' : (int)$yearParam;

$page = isset($_GET['page']) ? (int)$_GET['page'] : 1;
if ($page < 1) $page = 1;
$perPage = 10;
$offset  = ($page-1)*$perPage;

// WHERE helpers
$yrWO  = ($selYear==='all') ? '' : ' AND YEAR(WOreqOn) = '.$selYear.' ';
$yrSUB = ($selYear==='all') ? '' : ' AND YEAR(submittedOn) = '.$selYear.' ';

//////////////// Years list ////////////////
$years = [];
if ($rsYears = safeQuery("
  SELECT yr FROM (
    SELECT DISTINCT YEAR(WOreqOn)     AS yr FROM mentain_workshop WHERE WOreqOn     IS NOT NULL
    UNION
    SELECT DISTINCT YEAR(submittedOn) AS yr FROM mentain_workshop WHERE submittedOn IS NOT NULL
  ) y WHERE yr IS NOT NULL ORDER BY yr DESC
")){
  while($row=$rsYears->fetchAssoc()){ $years[]=(int)$row['yr']; }
}
if(!$years){ $years=[(int)date('Y')]; }

//////////////// Maintenance Summary ////////////////
// Total WOs (by request date)
$row = fetchOne("SELECT COUNT(*) AS cnt FROM mentain_workshop WHERE 1=1 $yrWO");
$total_wo = (int)($row['cnt'] ?? 0);

// Ongoing & Completed
$row = fetchOne("
  SELECT 
    SUM(CASE WHEN status=3 AND ".($selYear==='all'?"1":"YEAR(WOreqOn)={$selYear}")." THEN 1 ELSE 0 END) AS ongoing,
    SUM(CASE WHEN status=4 AND ".($selYear==='all'?"1":"YEAR(submittedOn)={$selYear}")." THEN 1 ELSE 0 END) AS completed
  FROM mentain_workshop
");
$ongoing   = (int)($row['ongoing']   ?? 0);
$completed = (int)($row['completed'] ?? 0);

// Avg TAT (completed)
$row = fetchOne("
  SELECT AVG(DATEDIFF(DATE(submittedOn), DATE(WOreqOn))) AS avg_tat
  FROM mentain_workshop
  WHERE status=4 AND submittedOn IS NOT NULL AND WOreqOn IS NOT NULL $yrSUB
");
$avg_tat = $row && $row['avg_tat']!==null ? round((float)$row['avg_tat'],1) : 0.0;

// Pending & Overdue
$row = fetchOne("
  SELECT 
    COUNT(*) AS pending,
    SUM(CASE WHEN WOreqOn IS NOT NULL AND DATEDIFF(CURDATE(), DATE(WOreqOn))>14 THEN 1 ELSE 0 END) AS overdue
  FROM mentain_workshop
  WHERE status=3 $yrWO
");
$pending = (int)($row['pending'] ?? 0);
$overdue = (int)($row['overdue'] ?? 0);

// Top vehicle (completed)
$row = fetchOne("
  SELECT vehNo, COUNT(*) AS cnt
  FROM mentain_workshop
  WHERE status=4 AND submittedOn IS NOT NULL $yrSUB
  GROUP BY vehNo ORDER BY cnt DESC, vehNo LIMIT 1
");
$vehNo  = trim((string)($row['vehNo'] ?? ''));
$vehCnt = (int)($row['cnt'] ?? 0);
$top_vehicle = $vehNo ? h($vehNo)." (".$vehCnt." WOs)" : "‚Äî";

//////////////// Trend (cumulative) //////////////////
$labels=[]; $reqMonthly=array_fill(0,12,0); $doneMonthly=array_fill(0,12,0);

if($selYear==='all'){
  $start = date('Y-m-01', strtotime('-11 months')); $end = date('Y-m-t');
  $mapIndex=[];
  for($i=0;$i<12;$i++){ $ts=strtotime("+$i month",strtotime($start)); $labels[$i]=date('M Y',$ts); $mapIndex[date('Y-m',$ts)]=$i; }
  if($rs = safeQuery("
    SELECT DATE_FORMAT(WOreqOn,'%Y-%m') ym, COUNT(*) cnt
    FROM mentain_workshop
    WHERE WOreqOn IS NOT NULL AND DATE(WOreqOn) BETWEEN '$start' AND '$end' GROUP BY ym
  ")){
    while($r=$rs->fetchAssoc()){ if(isset($mapIndex[$r['ym']])) $reqMonthly[$mapIndex[$r['ym']]]=(int)$r['cnt']; }
  }
  if($rs = safeQuery("
    SELECT DATE_FORMAT(submittedOn,'%Y-%m') ym, COUNT(*) cnt
    FROM mentain_workshop
    WHERE status=4 AND submittedOn IS NOT NULL AND DATE(submittedOn) BETWEEN '$start' AND '$end' GROUP BY ym
  ")){
    while($r=$rs->fetchAssoc()){ if(isset($mapIndex[$r['ym']])) $doneMonthly[$mapIndex[$r['ym']]]=(int)$r['cnt']; }
  }
  $trendTitle='Trend (Last 12 Months)';
}else{
  $labels=['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
  if($rs=safeQuery("SELECT MONTH(WOreqOn) m, COUNT(*) cnt FROM mentain_workshop WHERE WOreqOn IS NOT NULL AND YEAR(WOreqOn)={$selYear} GROUP BY m")){
    while($r=$rs->fetchAssoc()){ $idx=max(0,min(11,(int)$r['m']-1)); $reqMonthly[$idx]=(int)$r['cnt']; }
  }
  if($rs=safeQuery("SELECT MONTH(submittedOn) m, COUNT(*) cnt FROM mentain_workshop WHERE status=4 AND submittedOn IS NOT NULL AND YEAR(submittedOn)={$selYear} GROUP BY m")){
    while($r=$rs->fetchAssoc()){ $idx=max(0,min(11,(int)$r['m']-1)); $doneMonthly[$idx]=(int)$r['cnt']; }
  }
  $trendTitle='Trend ('.$selYear.')';
}
$seriesTotal=[]; $seriesDone=[]; $seriesOut=[]; $runReq=0; $runDone=0;
for($i=0;$i<12;$i++){ $runReq+=$reqMonthly[$i]; $runDone+=$doneMonthly[$i]; $seriesTotal[$i]=$runReq; $seriesDone[$i]=$runDone; $seriesOut[$i]=max(0,$runReq-$runDone); }

//////////////// Cross-tab: Vehicles √ó Months (Completed) //////////////////
$yearClause = ($selYear==='all') ? '' : " AND YEAR(submittedOn)=$selYear ";
$totalRows=0;
if($r=fetchOne("SELECT COUNT(*) AS cnt FROM (SELECT vehNo FROM mentain_workshop WHERE status=4 AND submittedOn IS NOT NULL $yearClause GROUP BY vehNo) t")) $totalRows=(int)$r['cnt'];
$totalPages=max(1,(int)ceil($totalRows/$perPage));
if($page>$totalPages){ $page=$totalPages; $offset=($page-1)*$perPage; }

$crosstabRows=[];
if($rs = safeQuery("
  SELECT vehNo,
    SUM(CASE WHEN MONTH(submittedOn)=1  THEN 1 ELSE 0 END) AS Jan,
    SUM(CASE WHEN MONTH(submittedOn)=2  THEN 1 ELSE 0 END) AS Feb,
    SUM(CASE WHEN MONTH(submittedOn)=3  THEN 1 ELSE 0 END) AS Mar,
    SUM(CASE WHEN MONTH(submittedOn)=4  THEN 1 ELSE 0 END) AS Apr,
    SUM(CASE WHEN MONTH(submittedOn)=5  THEN 1 ELSE 0 END) AS May,
    SUM(CASE WHEN MONTH(submittedOn)=6  THEN 1 ELSE 0 END) AS Jun,
    SUM(CASE WHEN MONTH(submittedOn)=7  THEN 1 ELSE 0 END) AS Jul,
    SUM(CASE WHEN MONTH(submittedOn)=8  THEN 1 ELSE 0 END) AS Aug,
    SUM(CASE WHEN MONTH(submittedOn)=9  THEN 1 ELSE 0 END) AS Sep,
    SUM(CASE WHEN MONTH(submittedOn)=10 THEN 1 ELSE 0 END) AS Oct,
    SUM(CASE WHEN MONTH(submittedOn)=11 THEN 1 ELSE 0 END) AS Nov,
    SUM(CASE WHEN MONTH(submittedOn)=12 THEN 1 ELSE 0 END) AS `Dec`,
    COUNT(*) AS Total
  FROM mentain_workshop
  WHERE status=4 AND submittedOn IS NOT NULL $yearClause
  GROUP BY vehNo
  ORDER BY Total DESC, vehNo
  LIMIT $offset, $perPage
")){
  while($r=$rs->fetchAssoc()){ $crosstabRows[]=$r; }
}
$qsYear = ($selYear==='all') ? 'all' : (string)$selYear;

//////////////// Region dictionary (from `regions` table) //////////////////

// Build mapping: REGION_ID -> Region Name (prefer `REGION`, fallback `region_name`)
$regionDict = [];

$sqlRegionDict = "
  SELECT 
    (REGION_ID+0) AS rid,
    COALESCE(
      NULLIF(TRIM(REGION), ''),        -- primary column with the human-readable name
      NULLIF(TRIM(region_name), '')    -- fallback if present
    ) AS nm
  FROM regions
  WHERE REGION_ID IS NOT NULL
";
if ($rs = safeQuery($sqlRegionDict)) {
  while ($r = $rs->fetchAssoc()) {
    $rid = (int)$r['rid'];
    $nm  = isset($r['nm']) ? trim((string)$r['nm']) : '';
    if ($rid && $nm !== '') $regionDict[$rid] = $nm;
  }
}

// Helper to resolve final display name everywhere
function regionNameFromDict($rid, $dict){
  $rid = (int)$rid;
  if (isset($dict[$rid]) && $dict[$rid] !== '') return $dict[$rid];
  return 'Region '.$rid;  // only used if your regions table truly lacks a name
}


//////////////// Commission Snapshot (STATION only) //////////////////
// Use dictionary for names
$regionNames = $regionDict;

$commRegionsStats=[]; $commStationsByReg=[]; $commTotStations=0; $commTotOOC=0; $commOOC_workshop=0; $commOOC_noveh=0;
if($rsSt = safeQuery("
  SELECT s.REGION_ID, s.STATION, s.VehicleNo, s.Avail, s.gps,
    CASE
      WHEN s.VehicleNo IS NULL OR s.VehicleNo='' THEN 1
      WHEN EXISTS (SELECT 1 FROM mentain_workshop w WHERE w.Station=s.STATION AND w.status=3 LIMIT 1) THEN 1
      ELSE 0
    END AS is_ooc,
    CASE
      WHEN s.VehicleNo IS NULL OR s.VehicleNo='' THEN 'No Vehicle'
      WHEN EXISTS (SELECT 1 FROM mentain_workshop w2 WHERE w2.Station=s.STATION AND w2.status=3 LIMIT 1) THEN 'In Workshop'
      ELSE 'Working'
    END AS ooc_reason
  FROM stations s
  WHERE UPPER(TRIM(COALESCE(s.OfficeType,'')))='STATION'
")){
  while($row=$rsSt->fetchAssoc()){
    $rid=(int)$row['REGION_ID']; $rname=regionNameFromDict($rid,$regionDict);
    if(!isset($commRegionsStats[$rid])){ $commRegionsStats[$rid]=['name'=>$rname,'total'=>0,'working'=>0,'ooc'=>0]; $commStationsByReg[$rid]=[]; }
    $is_ooc=((int)$row['is_ooc']===1);
    $commRegionsStats[$rid]['total']++; $commTotStations++;
    if($is_ooc){ $commRegionsStats[$rid]['ooc']++; $commTotOOC++; if($row['ooc_reason']==='In Workshop') $commOOC_workshop++; if($row['ooc_reason']==='No Vehicle') $commOOC_noveh++; }
    $commStationsByReg[$rid][]=['station'=>$row['STATION'],'vehicle'=>$row['VehicleNo'],'gps'=>$row['gps'],'avail'=>$row['Avail'],'status'=>$row['ooc_reason'],'is_ooc'=>$is_ooc?1:0];
  }
}
foreach($commRegionsStats as $rid=>$st){ $commRegionsStats[$rid]['working']=$st['total']-$st['ooc']; }
$commTotWorking=$commTotStations-$commTotOOC;
$commWorkPct = ($commTotStations>0) ? number_format(($commTotWorking/$commTotStations)*100,1) : 0;

// JS data for commission table
$jsCommRegions=[];
foreach($commRegionsStats as $rid=>$st){
  $jsCommRegions[]=[
    'region_id'=>$rid,
    'name'=>regionNameFromDict($rid,$regionDict),
    'total'=>(int)$st['total'],
    'working'=>(int)$st['working'],
    'ooc'=>(int)$st['ooc'],
    'work_rate'=>($st['total']>0?round(($st['working']/$st['total'])*100,1):0),
    'ooc_rate' =>($st['total']>0?round(($st['ooc']    /$st['total'])*100,1):0)
  ];
}

//////////////// Regional breakdown dataset (cards & table) ////////////////
$yrWOfilter = ($selYear==='all') ? '' : " AND YEAR(w.WOreqOn)=$selYear ";

$sqlRB = "
  SELECT 
    (s.REGION_ID+0) AS rid,
    SUM(CASE WHEN w.status=3 THEN 1 ELSE 0 END) AS pending,
    SUM(CASE WHEN w.status=4 THEN 1 ELSE 0 END) AS completed,
    SUM(CASE WHEN w.status=3 AND w.WOreqOn IS NOT NULL 
             AND DATEDIFF(CURDATE(), DATE(w.WOreqOn))>14 THEN 1 ELSE 0 END) AS overdue,
    COUNT(*) AS total,
    ROUND(AVG(CASE 
      WHEN w.status=4 AND w.submittedOn IS NOT NULL AND w.WOreqOn IS NOT NULL 
      THEN DATEDIFF(DATE(w.submittedOn), DATE(w.WOreqOn)) END),1) AS avg_tat
  FROM stations s
  JOIN mentain_workshop w ON w.Station = s.STATION
  WHERE UPPER(TRIM(COALESCE(s.OfficeType,'')))='STATION'
    AND w.WOreqOn IS NOT NULL
    $yrWOfilter
  GROUP BY rid
  ORDER BY rid
";

$regionWOStats=[];
if ($rsRB = safeQuery($sqlRB)) {
  while ($r = $rsRB->fetchAssoc()) {
    $rid          = (int)$r['rid'];
    $pending_wos  = (int)($r['pending']   ?? 0);
    $completed_wos= (int)($r['completed'] ?? 0);
    $overdue_wos  = (int)($r['overdue']   ?? 0);
    $total_wos    = (int)($r['total']     ?? 0);

    $regionWOStats[] = [
      'region'     => regionNameFromDict($rid, $regionDict),
      'pending'    => $pending_wos,
      'completed'  => $completed_wos,
      'overdue'    => $overdue_wos,
      'total'      => $total_wos,
      'avg_tat'    => ($r['avg_tat']===null ? null : (float)$r['avg_tat']),
      'pend_rate'  => ($total_wos>0? round(($pending_wos/$total_wos)*100,1):0),
      'comp_rate'  => ($total_wos>0? round(($completed_wos/$total_wos)*100,1):0)
    ];
  }
}
usort($regionWOStats, function($a,$b){ return strcmp($a['region'],$b['region']); });

//////////////// HTML ///////////////////
echo '
<style>
  .vmcd-wrap { font-family: system-ui,-apple-system,Segoe UI,Roboto,Arial; padding:20px; }
  .toolbar { display:flex; flex-wrap:wrap; gap:10px; align-items:center; margin-bottom:12px; }
  .toolbar .chip { display:flex; align-items:center; gap:6px; padding:8px 10px; border:1px solid #e5e7eb; border-radius:999px; background:#fff; font-size:13px; }
  .panel { border:1px solid #e5e7eb; border-radius:14px; background:#fff; padding:16px; }
  .panel + .panel { margin-top:16px; }
  .panel h3 { margin:0 0 10px; font-size:16px; }
  .cards { display:flex; flex-wrap:wrap; gap:16px; }
  .card { flex:1; min-width:220px; border-radius:12px; padding:16px; transition:.15s; }
  .card[data-key]{ cursor:pointer; }
  .card[data-key]:hover{ transform:scale(1.02); box-shadow:0 3px 10px rgba(0,0,0,.1); }
  .card h4 { margin:0 0 8px; font-size:14px; font-weight:600; }
  .muted { color:#6b7280; }
  .btn { padding:6px 10px; border-radius:8px; border:1px solid #d0d7de; background:#f6f8fa; cursor:pointer; }
  .btn-alt { background:#fff1f2; border-color:#ffd0d0; }
  .btn-ok  { background:#e8f5e9; border-color:#b7ebc6; }
  table.clean { width:100%; border-collapse:collapse; font-size:14px; }
  table.clean th, table.clean td { padding:10px 12px; border-bottom:1px solid #f1f5f9; }
  table.clean thead th { background:#f9fafb; border-bottom:1px solid #e5e7eb; }
  .pill { display:inline-block; padding:.25rem .55rem; border-radius:999px; font-size:.85rem; font-weight:600; border:1px solid transparent; }
  .pill-ok  { background:#e6f4ea; color:#0f5132; border-color:#badbcc; }
  .pill-warn{ background:#fff3cd; color:#8a6d3b; border-color:#ffe69c; }
  .pill-bad { background:#fee2e2; color:#991b1b; border-color:#fecaca; }
  .section-title { display:flex; align-items:center; justify-content:space-between; margin-bottom:8px; }
  .section-title small { color:#6b7280; font-weight:500; }
</style>

<div class="vmcd-wrap">
  <h2 style="margin:0 0 10px">üöë Vehicle Maintenance & Commission Dashboard</h2>

  <!-- View Controls -->
  <div class="toolbar panel" id="sec-controls">
    <div class="muted" style="margin-right:6px;">View:</div>
    <label class="chip"><input type="checkbox" data-target="#sec-maint" checked> Maintenance KPIs</label>
    <label class="chip"><input type="checkbox" data-target="#sec-commission" checked> Commission Snapshot</label>
    <label class="chip"><input type="checkbox" data-target="#sec-region-wo" checked> Regional WOs</label>
    <label class="chip"><input type="checkbox" data-target="#sec-trend" checked> Trend Chart</label>
    <label class="chip"><input type="checkbox" data-target="#sec-crosstab"> Completed by Vehicle</label>

    <div style="margin-left:auto; display:flex; gap:8px; align-items:center;">
      <form method="get" style="display:flex; gap:8px; align-items:center; margin:0;">
        <label for="year" class="muted">Year</label>
        <select name="year" id="year" onchange="this.form.submit()" class="btn" style="padding:.4rem .6rem;">
          <option value="all"'.($selYear==='all'?' selected':'').'>All years</option>';
          foreach($years as $y){ echo '<option value="'.h($y).'"'.($selYear===$y?' selected':'').'>'.h($y).'</option>'; }
echo '   </select>
        <noscript><button type="submit" class="btn">Apply</button></noscript>
      </form>
    </div>
  </div>

  <!-- Maintenance KPIs -->
  <section id="sec-maint" class="panel">
    <div class="section-title"><h3>üß∞ Maintenance Summary</h3><small class="muted">'.($selYear==='all'?'Last 12 months view for chart':'Year '.$selYear).'</small></div>
    <div class="cards">
      <div class="card" style="background:#f0f4f8;" data-key="total">
        <h4>Total Work Orders'.($selYear==='all'?'':' ('.$selYear.')').'</h4>
        <div style="font-size:28px; font-weight:800;">'.fmtInt($total_wo).'</div>
      </div>
      <div class="card" style="background:#e8f5e9;" data-key="status">
        <h4>Work Order Status</h4>
        <div><b>Ongoing:</b> '.fmtInt($ongoing).'</div>
        <div><b>Completed:</b> '.fmtInt($completed).'</div>
      </div>
      <div class="card" style="background:#fff3e0;" data-key="tat">
        <h4>Avg Turnaround Time</h4>
        <div style="font-size:28px; font-weight:800;">'.fmtFloat($avg_tat).' days</div>
      </div>
      <div class="card" style="background:#ffebee;" data-key="pending">
        <h4>Pending / Overdue</h4>
        <div><b>Pending:</b> '.fmtInt($pending).'</div>
        <div><b>Overdue &gt;14d:</b> '.fmtInt($overdue).'</div>
      </div>
      <div class="card" style="background:#ede7f6;">
        <h4>Top Maintained Vehicle</h4>
        <div>'.h($top_vehicle).'</div>
      </div>
    </div>
  </section>

  <!-- Commission Snapshot -->
  <section id="sec-commission" class="panel">
    <div class="section-title"><h3>üö¶ Commission Snapshot (OfficeType = STATION)</h3></div>
    <div class="cards" style="margin-bottom:10px;">
      <div class="card" style="background:#f0f4f8;"><h4>Total Stations (Vehicles)</h4><div style="font-size:28px; font-weight:800;">'.fmtInt($commTotStations).'</div></div>
      <div class="card" style="background:#e8f5e9;"><h4>In Commission (Working)</h4><div style="font-size:28px; font-weight:800;">'.fmtInt($commTotWorking).' <span class="muted">('.$commWorkPct.'%)</span></div></div>
      <div class="card" style="background:#ffebee;"><h4>Out of Commission</h4><div style="font-size:28px; font-weight:800;">'.fmtInt($commTotOOC).' <span class="muted">('.($commTotStations>0? number_format(($commTotOOC/$commTotStations)*100,1):0).'%)</span></div><div class="muted" style="margin-top:6px;">In Workshop: '.fmtInt($commOOC_workshop).' ¬∑ No Vehicle: '.fmtInt($commOOC_noveh).'</div></div>
    </div>

    <div class="panel" style="padding:0;">
      <table class="clean">
        <thead>
          <tr>
            <th style="text-align:left;">Region</th>
            <th style="text-align:right;">Working</th>
            <th style="text-align:right;">OOC</th>
            <th style="text-align:right;">Total</th>
            <th style="text-align:right;">In Comm %</th>
            <th style="text-align:right;">OOC %</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody id="commissionRegionRows"></tbody>
      </table>
    </div>
  </section>

  <!-- Regional Work Orders table -->
  <section id="sec-region-wo" class="panel">
    <div class="section-title"><h3>üóÇÔ∏è Regional Work Orders ‚Äî Pending vs Completed</h3><small class="muted">'.($selYear==='all'?'By request date (last 12m aggregate)':'By request date ('.$selYear.')').'</small></div>
    <div class="panel" style="padding:0;">
      <table class="clean">
        <thead>
          <tr>
            <th style="text-align:left;">Region</th>
            <th style="text-align:right;">Pending WOs</th>
            <th style="text-align:right;">Completed WOs</th>
            <th style="text-align:right;">Overdue &gt;14d</th>
            <th style="text-align:right;">Total WOs</th>
            <th style="text-align:right;">Pending %</th>
            <th style="text-align:right;">Completed %</th>
            <th style="text-align:right;">Avg TAT (d)</th>
          </tr>
        </thead>
        <tbody>';
          if(!$regionWOStats){
            echo '<tr><td colspan="8" class="muted" style="text-align:center; padding:14px;">No work orders found for the selected period.</td></tr>';
          } else {
            foreach($regionWOStats as $rw){
              echo '<tr>
                <td>'.h($rw['region']).'</td>
                <td style="text-align:right;">'.fmtInt($rw['pending']).'</td>
                <td style="text-align:right;">'.fmtInt($rw['completed']).'</td>
                <td style="text-align:right;">'.fmtInt($rw['overdue']).'</td>
                <td style="text-align:right;">'.fmtInt($rw['total']).'</td>
                <td style="text-align:right;">'.fmtFloat($rw['pend_rate']).'%</td>
                <td style="text-align:right;">'.fmtFloat($rw['comp_rate']).'%</td>
                <td style="text-align:right;">'.($rw['avg_tat']===null?'‚Äî':fmtFloat($rw['avg_tat'])).'</td>
              </tr>';
            }
          }
echo '   </tbody>
      </table>
    </div>
  </section>

  <!-- Trend -->
  <section id="sec-trend" class="panel">
    <div class="section-title"><h3>üìà Requested vs Completed vs Outstanding ‚Äî '.$trendTitle.'</h3></div>
    <div class="panel" style="padding:12px;">
      <canvas id="woTrendChart" height="120"></canvas>
    </div>
  </section>

  <!-- Cross-tab -->
  <section id="sec-crosstab" class="panel" style="display:none;">
    <div class="section-title"><h3>‚úÖ Completed Work Orders by Vehicle and Month'.($selYear==='all'?'':' ‚Äî '.$selYear).'</h3></div>
    <div class="panel" style="padding:0;">
      <table class="clean" style="min-width:900px;">
        <thead>
          <tr>
            <th style="text-align:left;">Vehicle</th>
            <th>Jan</th><th>Feb</th><th>Mar</th><th>Apr</th><th>May</th><th>Jun</th>
            <th>Jul</th><th>Aug</th><th>Sep</th><th>Oct</th><th>Nov</th><th>Dec</th>
