/* Vehicle Work History Lookup (mentain_workshop + crew/parts names)
 * - Crew names from nas_pcr.stafff
 * - Spare part names from stores.stock_bal_region (prod_name)
 * - Robust product-name resolution: RAW / NORMALIZED / EXTRACTED code
 * - Works for parts_used rows and free-text fallback
 */

//////////////////////////// Config ////////////////////////////
$STAFF_DB  = 'nas_pcr';   // schema with stafff
$STORES_DB = 'stores';    // schema with stock_bal_region

//////////////////////////// Helpers ////////////////////////////
function fetchOne($sql){ $rs = DB::Query($sql); return $rs ? $rs->fetchAssoc() : null; }
function fetchAll($sql){ $out=[]; $rs=DB::Query($sql); while($rs && ($row=$rs->fetchAssoc())) $out[]=$row; return $out; }
function h($s){ return htmlspecialchars((string)$s, ENT_QUOTES); }
function fmtInt($n){ return number_format((int)$n); }
function fmtDate($d){ if(!$d||$d=='0000-00-00'||$d=='0000-00-00 00:00:00') return 'â€”'; return date('M j, Y', strtotime($d)); }
function fmtDateTime($d){ if(!$d||$d=='0000-00-00 00:00:00') return 'â€”'; return date('M j, Y H:i', strtotime($d)); }
function statusText($s){ if($s==2) return 'Work Order Pending'; if($s==3) return 'Work Order Submitted'; if($s==4) return 'Vehicle Ready'; return (string)$s; }
function statusClass($s){ if($s==4) return 'ok'; if($s==3) return 'warn'; if($s==2) return 'bad'; return 'neutral'; }
function getParam($name,$default=''){ if(function_exists('postvalue')){ $v=postvalue($name); if($v!==null && $v!=='') return $v; } return isset($_REQUEST[$name])?$_REQUEST[$name]:$default; }

/* Product code normalizers */
function norm_code($s){ return strtoupper(preg_replace('/[^A-Z0-9]/', '', (string)$s)); }
function raw_code($s){ return strtoupper(trim((string)$s)); }
function extract_code($s){ $u = strtoupper((string)$s); if(preg_match('/[A-Z0-9]+/',$u,$m)) return $m[0]; return norm_code($u); }
function sql_in_list($arr){
  if(!$arr) return "('')";
  $vals = array_map(function($v){ return "'".addslashes($v)."'"; }, array_values($arr));
  return "(".implode(",", $vals).")";
}

//////////////////////////// Inputs ////////////////////////////
$vehRaw  = trim((string)getParam('veh',''));
$pg      = (int)getParam('pg',1); if($pg<1) $pg=1;
$perPage = 15;
$offset  = ($pg-1)*$perPage;

//////////////////////////// Filter (vehNo + chassis) ////////////////////////////
$vehNorm = strtoupper(preg_replace('/[^A-Z0-9]/', '', $vehRaw));
$strip = "REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(UPPER(%s), '-', ''), ' ', ''), '/', ''), '.', ''), '_', ''), ':', ''), '\t','')";
$where = '';
if($vehRaw!==''){
  $vehEsc     = addslashes($vehRaw);
  $vehNormEsc = addslashes($vehNorm);
  $vehNoStripped  = sprintf($strip,"COALESCE(mw.vehNo,'')");
  $chasStripped   = sprintf($strip,"COALESCE(mw.chasisNo,'')");
  $where = "
    WHERE
      UPPER(COALESCE(mw.vehNo,'')) LIKE UPPER('%{$vehEsc}%')
      OR {$vehNoStripped} LIKE '%{$vehNormEsc}%'
      OR {$chasStripped}  LIKE '%{$vehNormEsc}%'
  ";
}

//////////////////////////// Count ////////////////////////////
$row = fetchOne("
  SELECT COUNT(*) AS cnt
  FROM mentain_workshop mw
  INNER JOIN stations s ON s.STATION = mw.Station
  INNER JOIN regions  r ON r.REGION_ID = s.REGION_ID
  $where
");
$totalRows  = (int)($row['cnt'] ?? 0);
$totalPages = max(1,(int)ceil($totalRows/$perPage));
if($pg>$totalPages){ $pg=$totalPages; $offset=($pg-1)*$perPage; }

//////////////////////////// Page Results ////////////////////////////
$list = fetchAll("
  SELECT
    mw.reqID AS work_id,
    mw.Station, mw.vehNo, mw.servicetype, mw.description, mw.KMR,
    mw.requestedby, mw.requestedon, mw.arrivaltime, mw.arrivaldate,
    mw.vehType, mw.chasisNo, mw.repairInstruction, mw.KMRin, mw.KMRout,
    mw.WOreqBy, mw.WOreqOn, mw.status,
    mw.workorder_id, mw.workDate, mw.partsUsed, mw.reccomendation, mw.submittedby, mw.submittedOn,
    CASE
      WHEN mw.submittedOn IS NULL OR mw.submittedOn='' THEN DATEDIFF(CURDATE(), DATE(mw.WOreqOn))
      ELSE DATEDIFF(DATE(mw.submittedOn), DATE(mw.WOreqOn))
    END AS total_days_at_wkshop,
    s.REGION_ID, r.REGION
  FROM mentain_workshop mw
  INNER JOIN stations s ON s.STATION   = mw.Station
  INNER JOIN regions  r ON r.REGION_ID = s.REGION_ID
  $where
  ORDER BY mw.reqID DESC
  LIMIT $offset, $perPage
");

//////////////////////////// Prefetch CREW ////////////////////////////
$workIds = array_map(function($r){ return (int)$r['work_id']; }, $list);
$crewByWork = []; 
$partsByWork = [];

if ($workIds){
  $ids = implode(',', array_map('intval',$workIds));

  $crewRows = fetchAll("
    SELECT  c.ID,
            mw.reqID AS work_id,
            c.work_id AS source_work_id,
            c.staffID, c.wordDone,
            sf.SURNAMES, sf.FIRSTNAMES, sf.`MIDDLE NAME` AS MIDDLENAME
    FROM mentain_workshop mw
    JOIN crew_workdone c
      ON (c.work_id = mw.reqID OR c.work_id = mw.workorder_id)
    LEFT JOIN `{$STAFF_DB}`.`stafff` sf
      ON UPPER(TRIM(sf.username)) = UPPER(TRIM(c.staffID))
    WHERE mw.reqID IN ($ids)
    ORDER BY c.ID ASC
  ");
  foreach($crewRows as $cr){
    $wid = (int)$cr['work_id'];
    if(!isset($crewByWork[$wid])) $crewByWork[$wid]=[];
    $full = trim(preg_replace('/\s+/', ' ', ($cr['FIRSTNAMES']?:'').' '.($cr['MIDDLENAME']?:'').' '.($cr['SURNAMES']?:'')));
    if($full==='') $full = (string)$cr['staffID'];
    $crewByWork[$wid][] = [
      'staffID'   => (string)$cr['staffID'],
      'staffName' => $full,
      'wordDone'  => (string)$cr['wordDone']
    ];
  }
}

//////////////////////////// Prefetch PARTS (rows + fallback + tolerant name map) ////////////////////////////
$productCodesAll = [];  // collect codes to resolve once
if ($workIds){
  $ids = implode(',', array_map('intval',$workIds));

  // 1) parts_used rows (reqID OR workorder_id)
  $partRows = fetchAll("
    SELECT  p.ID,
            mw.reqID AS work_id,
            p.work_id AS source_work_id,
            p.sparePart AS product_id,
            p.quantity
    FROM mentain_workshop mw
    JOIN parts_used p
      ON (p.work_id = mw.reqID OR p.work_id = mw.workorder_id)
    WHERE mw.reqID IN ($ids)
    ORDER BY p.ID ASC
  ");
  foreach($partRows as $pr){
    $wid = (int)$pr['work_id'];
    if(!isset($partsByWork[$wid])) $partsByWork[$wid]=[];
    $pid = trim((string)$pr['product_id']);
    $partsByWork[$wid][] = [
      'product_id' => $pid,
      'prod_name'  => '',                 // fill later
      'quantity'   => (string)$pr['quantity'] === '' ? null : (int)$pr['quantity']
    ];
    if($pid!=='') $productCodesAll[] = $pid;
  }

  // 2) Fallback: parse free-text 'partsUsed' where no relational rows were found
  foreach ($list as $rec){
    $wid = (int)$rec['work_id'];
    if (!isset($partsByWork[$wid]) || !count($partsByWork[$wid])){
      $raw = (string)$rec['partsUsed'];
      if ($raw !== ''){
        $tokens = preg_split('/[,\;\s]+/', $raw, -1, PREG_SPLIT_NO_EMPTY);
        $seen = [];
        foreach ($tokens as $t){
          $code = trim($t);
          if ($code==='') continue;
          $base = extract_code($code);
          if (isset($seen[$base])) continue; $seen[$base] = true;
          if(!isset($partsByWork[$wid])) $partsByWork[$wid]=[];
          $partsByWork[$wid][] = [
            'product_id' => $code,  // keep original text
            'prod_name'  => '',     // fill later
            'quantity'   => null
          ];
          $productCodesAll[] = $code;
        }
      }
    }
  }

  // 3) Build compact name map from stores.stock_bal_region for the relevant codes only
  $rawSet  = []; $normSet = []; $exSet = [];
  foreach ($productCodesAll as $c){
    $rawSet[ raw_code($c) ] = true;
    $normSet[ norm_code($c) ] = true;
    $exSet[ extract_code($c) ] = true;
  }
  $inRaw  = sql_in_list(array_filter(array_keys($rawSet)));
  $inNorm = sql_in_list(array_filter(array_keys($normSet)));
  $inEx   = sql_in_list(array_filter(array_keys($exSet)));

  $mapRows = fetchAll("
    SELECT
      product AS product_id_raw,
      UPPER(TRIM(product)) AS key_raw,
      REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(UPPER(product),'-',''),' ',''),'/', ''),'.',''),'_',''),':','') AS key_norm,
      MAX(prod_name) AS product_name
    FROM `{$STORES_DB}`.`stock_bal_region`
    WHERE UPPER(TRIM(product)) IN $inRaw
       OR REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(REPLACE(UPPER(product),'-',''),' ',''),'/', ''),'.',''),'_',''),':','') IN $inNorm
       OR UPPER(TRIM(product)) IN $inEx
    GROUP BY product_id_raw, key_raw, key_norm
  ");

  $nameByRaw  = [];
  $nameByNorm = [];
  foreach ($mapRows as $mr){
    $nm = (string)$mr['product_name'];                // â† prod_name aliased
    $rawKey  = strtoupper(trim((string)$mr['key_raw']));
    $normKey = strtoupper(trim((string)$mr['key_norm']));
    if ($rawKey  !== '') $nameByRaw[$rawKey]   = $nm;
    if ($normKey !== '') $nameByNorm[$normKey] = $nm;
  }

  // 4) Assign names using RAW â†’ NORM â†’ EXTRACT fallback
  foreach ($partsByWork as $wid => $arr){
    foreach ($arr as $i => $p){
      $raw  = raw_code($p['product_id']);
      $norm = norm_code($p['product_id']);
      $ex   = extract_code($p['product_id']);
      $nm = '';
      if (isset($nameByRaw[$raw]))        $nm = $nameByRaw[$raw];
      else if (isset($nameByNorm[$norm])) $nm = $nameByNorm[$norm];
      else {
        $exRaw  = raw_code($ex);
        $exNorm = norm_code($ex);
        if (isset($nameByRaw[$exRaw]))        $nm = $nameByRaw[$exRaw];
        else if (isset($nameByNorm[$exNorm])) $nm = $nameByNorm[$exNorm];
      }
      $partsByWork[$wid][$i]['prod_name'] = ($nm !== '') ? $nm : $p['product_id'];
    }
  }
}

//////////////////////////// JSON payload ////////////////////////////
$payload = [];
foreach($list as $r){
  $wid = (int)$r['work_id'];
  $arrivalTxt = trim(($r['arrivaldate'] ? fmtDate($r['arrivaldate']) : 'â€”').' '.($r['arrivaltime'] ?: ''));
  $payload[] = [
    'work_id'      => $wid,
    'Station'      => (string)$r['Station'],
    'REGION'       => (string)$r['REGION'],
    'vehNo'        => (string)$r['vehNo'],
    'vehType'      => (string)$r['vehType'],
    'servicetype'  => (string)$r['servicetype'],
    'description'  => (string)$r['description'],
    'KMR'          => (string)$r['KMR'],
    'requestedon'  => (string)fmtDateTime($r['requestedon']),
    'requestedby'  => (string)$r['requestedby'],
    'WOreqOn'      => (string)fmtDateTime($r['WOreqOn']),
    'WOreqBy'      => (string)$r['WOreqBy'],
    'arrival'      => (string)$arrivalTxt,
    'chasisNo'     => (string)$r['chasisNo'],
    'repairInstruction' => (string)$r['repairInstruction'],
    'KMRin'        => (string)$r['KMRin'],
    'KMRout'       => (string)$r['KMRout'],
    'status_code'  => (int)$r['status'],
    'status_text'  => statusText($r['status']),
    'status_class' => statusClass($r['status']),
    'workorder_id' => (string)$r['workorder_id'],
    'workDate'     => (string)fmtDate($r['workDate']),
    'partsUsed'    => (string)$r['partsUsed'],
    'reccomendation'=> (string)$r['reccomendation'],
    'submittedby'  => (string)$r['submittedby'],
    'submittedOn'  => (string)fmtDateTime($r['submittedOn']),
    'total_days_at_wkshop' => (int)max(0,(int)$r['total_days_at_wkshop']),
    'crew'         => isset($crewByWork[$wid])  ? $crewByWork[$wid]  : [],
    'parts'        => isset($partsByWork[$wid]) ? $partsByWork[$wid] : []
  ];
}
$json = json_encode($payload, JSON_HEX_TAG|JSON_HEX_AMP|JSON_HEX_APOS|JSON_HEX_QUOT);

//////////////////////////// Table rows ////////////////////////////
$rowsHtml = '';
if(!$list){
  $rowsHtml .= '<tr><td colspan="9" class="vh-muted" style="text-align:center; padding:16px;">No work orders found.</td></tr>';
}else{
  foreach($list as $r){
    $wid = (int)$r['work_id'];
    $rowsHtml .= '<tr>';
    $rowsHtml .= '<td>'.fmtInt($wid).'</td>';
    $rowsHtml .= '<td>'.h($r['vehNo']).'</td>';
    $rowsHtml .= '<td>'.h($r['Station']).'</td>';
    $rowsHtml .= '<td>'.h($r['REGION']).'</td>';
    $rowsHtml .= '<td>'.h($r['servicetype']).'</td>';
    $rowsHtml .= '<td>'.fmtDateTime($r['WOreqOn'] ?: $r['requestedon']).'</td>';
    $rowsHtml .= '<td>'.h(trim(($r['arrivaldate']?fmtDate($r['arrivaldate']):'â€”').' '.($r['arrivaltime']?:''))).'</td>';
    $rowsHtml .= '<td><span class="pill '.statusClass($r['status']).'">'.h(statusText($r['status'])).'</span></td>';
    $rowsHtml .= '<td class="vh-actions"><button type="button" class="vh-btn" onclick="openWorkModal('.$wid.')">View</button></td>';
    $rowsHtml .= '</tr>';
  }
}

//////////////////////////// Output (layout + modal) ////////////////////////////
echo <<<'HTML'
