echo '<script id="work-data" type="application/json">'.$json.'</script>';

echo <<<'HTML'
<script>
(function(){
  var vehVal = (new URLSearchParams(location.search)).get('veh') || '';
  var vehInput = document.getElementById('veh'); if(vehInput) vehInput.value = vehVal;

  var rowsOnPage = document.querySelectorAll('#vh-body tr').length;
  var countEl = document.getElementById('vh-count');
  if(countEl){
    countEl.innerHTML = (vehVal ? 'Results for <b>'+escapeHtml(vehVal)+'</b>: ' : '') + rowsOnPage + ' record(s) on this page';
  }

  var dataTag = document.getElementById('work-data');
  var workDetails = [];
  try { workDetails = JSON.parse(dataTag ? dataTag.textContent : '[]'); } catch(e){ workDetails = []; }

  window.openWorkModal = function(id){
    id = Number(id);
    var d = workDetails.find(function(x){ return Number(x.work_id)===id; });
    if(!d) return;

    document.getElementById('vh-modal-title').textContent = 'Work Details â€” #'+id;

    var sec1 = section('SERVICE REQUEST DETAILS <<', grid2([
      fld('Station', d.Station),          fld('Region', d.REGION),
      fld('Veh No', d.vehNo),             fld('Veh Type', d.vehType),
      fld('Servicetype', d.servicetype),  fld('Description', d.description),
      fld('KMR', d.KMR),                  null,
      fld('Requestedon', d.requestedon),  fld('Requestedby', d.requestedby)
    ]));

    var sec2 = section('WORK ORDER DETAILS <<', grid2([
      fld('Arrivaldate', d.arrival),          fld('Chasis No', d.chasisNo),
      fld('Repair Instruction', d.repairInstruction), fld('W Oreq On', d.WOreqOn),
      fld('Workorder Id', d.workorder_id),    fld('W Oreq By', d.WOreqBy)
    ]));

    var sec3 = section('ACTUAL WORK DONE <<', grid2([
      fld('Work Date', d.workDate),           fld('Parts Used', d.partsUsed),
      fld('Work Done', d.reccomendation),     fld('Submittedby', d.submittedby),
      fld('Submitted On', d.submittedOn),     fld('Total Days at Workshop', String(d.total_days_at_wkshop))
    ]));

    var crewRows = (d.crew||[]).map(function(c){
      return '<tr><td>'+escapeHtml(c.staffName||"")+'</td><td>'+escapeHtml(c.staffID||"")+'</td><td>'+escapeHtml(c.wordDone||"")+'</td></tr>';
    }).join('') || '<tr><td colspan="3" class="vh-muted">No crew recorded.</td></tr>';

    var partsRows = (d.parts||[]).map(function(p){
      var qty = (p.quantity==null || p.quantity===undefined || p.quantity==='') ? '-' : String(p.quantity);
      return '<tr><td>'+escapeHtml(p.prod_name||"")+'</td><td>'+escapeHtml(p.product_id||"")+'</td><td>'+escapeHtml(qty)+'</td></tr>';
    }).join('') || '<tr><td colspan="3" class="vh-muted">No spare parts recorded.</td></tr>';

    var sec4 = section('CREW THAT WORKED ON THE VEHICLE <<',
      '<div class="subtable"><table><thead><tr><th>Name</th><th>Staff ID</th><th>Work Done</th></tr></thead><tbody>'+crewRows+'</tbody></table></div>'
    );

    var sec5 = section('SPARE PARTS/CONSUMABLES USED <<',
      '<div class="subtable"><table><thead><tr><th>Spare Part</th><th>Product ID</th><th>Qty</th></tr></thead><tbody>'+partsRows+'</tbody></table></div>'
    );

    document.getElementById('vh-modal-body').innerHTML = sec1 + sec2 + sec3 + sec4 + sec5;
    document.getElementById('vh-modal-mask').style.display = 'flex';
  };

  window.closeWorkModal = function(){ document.getElementById('vh-modal-mask').style.display = 'none'; };

  function section(title, bodyHtml){
    return '<div class="sec"><div class="sec-hd">'+escapeHtml(title)+'</div><div class="sec-bd">'+bodyHtml+'</div></div>';
  }
  function grid2(arr){
    var cells = arr.map(function(x){
      if(!x) return '<div></div>';
      return '<div class="fld"><div class="lbl">'+escapeHtml(x.label)+'</div><div class="val">'+escapeHtml(x.value||"")+'</div></div>';
    }).join('');
    return '<div class="grid-2">'+cells+'</div>';
  }
  function fld(label,value){ return {label:label, value:value}; }
  function escapeHtml(s){ return String(s==null?'':s).replace(/&/g,'&amp;').replace(/</g,'&lt;').replace(/>/g,'&gt;').replace(/"/g,'&quot;').replace(/'/g,'&#039;'); }

  var form = document.getElementById('vh-search-form');
  var input = document.getElementById('veh');
  if(input && form){
    input.addEventListener('keydown', function(e){ if(e.key==='Enter'){ form.submit(); } });
  }
})();
</script>
