global $conn;

// Workorders Summary
$years = []; $year_totals = [];
$rs = DB::Query("SELECT year, SUM(total_workorders) as total FROM workorders_summary GROUP BY year ORDER BY year DESC");
while($data = $rs->fetchAssoc()) {
    $years[] = $data["year"];
    $year_totals[] = $data["total"];
}

// Top 5 staff
$staffNames = []; $staffCounts = [];
$rs = DB::Query("SELECT WOreqBy, SUM(total_workorders) as total FROM workorders_by_staff GROUP BY WOreqBy ORDER BY total DESC LIMIT 5");
while($data = $rs->fetchAssoc()) {
    $staffNames[] = $data["WOreqBy"];
    $staffCounts[] = $data["total"];
}

// Parts used
$partNames = []; $partCounts = [];
$rs = DB::Query("SELECT partsUsed, SUM(usage_count) as count FROM parts_usage_summary GROUP BY partsUsed ORDER BY count DESC LIMIT 5");
while($data = $rs->fetchAssoc()) {
    $partNames[] = $data["partsUsed"];
    $partCounts[] = $data["count"];
}

// Status distribution
$statusLabels = []; $statusCounts = [];
$rs = DB::Query("SELECT status, SUM(total) as total FROM status_distribution GROUP BY status");
while($data = $rs->fetchAssoc()) {
    $statusLabels[] = $data["status"];
    $statusCounts[] = $data["total"];
}

// Downtime summary
$downtimeLabels = []; $downtimeValues = [];
$rs = DB::Query("SELECT year, AVG(avg_downtime_days) as avg_downtime FROM downtime_summary GROUP BY year ORDER BY year");
while($data = $rs->fetchAssoc()) {
    $downtimeLabels[] = $data["year"];
    $downtimeValues[] = round($data["avg_downtime"], 2);
}

// Send JS data to HTML Snippet
echo "
<script>
const years = " . json_encode($years) . ";
const yearTotals = " . json_encode($year_totals) . ";
const staffLabels = " . json_encode($staffNames) . ";
const staffData = " . json_encode($staffCounts) . ";
const partsLabels = " . json_encode($partNames) . ";
const partsData = " . json_encode($partCounts) . ";
const statusLabels = " . json_encode($statusLabels) . ";
const statusData = " . json_encode($statusCounts) . ";
const downtimeLabels = " . json_encode($downtimeLabels) . ";
const downtimeData = " . json_encode($downtimeValues) . ";
</script>";

// Load Chart.js if not already loaded
let chartScript = document.createElement('script');
chartScript.src = "https://cdn.jsdelivr.net/npm/chart.js";
document.head.appendChild(chartScript);

// Wait for Chart.js to load, then render charts
chartScript.onload = function () {
    // Workorders
    new Chart(document.getElementById('workordersChart'), {
        type: 'bar',
        data: {
            labels: years,
            datasets: [{
                label: 'Total Work Orders',
                data: yearTotals,
                backgroundColor: 'rgba(13,110,253,0.7)'
            }]
        }
    });

    // Staff
    new Chart(document.getElementById('staffChart'), {
        type: 'bar',
        data: {
            labels: staffLabels,
            datasets: [{
                label: 'Requests by Staff',
                data: staffData,
                backgroundColor: 'rgba(25,135,84,0.7)'
            }]
        }
    });

    // Parts
    new Chart(document.getElementById('partsChart'), {
        type: 'pie',
        data: {
            labels: partsLabels,
            datasets: [{
                data: partsData,
                backgroundColor: ['#ffc107', '#fd7e14', '#198754', '#0dcaf0', '#6f42c1']
            }]
        }
    });

    // Status
    new Chart(document.getElementById('statusChart'), {
        type: 'doughnut',
        data: {
            labels: statusLabels,
            datasets: [{
                data: statusData,
                backgroundColor: ['#0d6efd', '#dc3545', '#ffc107', '#198754', '#6c757d']
            }]
        }
    });

    // Downtime
    new Chart(document.getElementById('downtimeChart'), {
        type: 'line',
        data: {
            labels: downtimeLabels,
            datasets: [{
                label: 'Avg Downtime (Days)',
                data: downtimeData,
                borderColor: 'rgba(220,53,69,0.9)',
                tension: 0.4
            }]
        }
    });
};

